mapper.select:
SELECT 

FROM test1 a

WHERE a.id = #{id}

SELECT 

FROM test1 a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.auth_name = #{authName}


		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM test1 a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE test1 SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
