mapper.select:
SELECT 

FROM bj_examination_item a

WHERE a.id = #{id}

SELECT 

FROM bj_examination_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{code}


		AND a.owner = #{owner}


		AND a.reference_flag = #{referenceFlag}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bj_examination_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bj_examination_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
