SELECT , i4.name AS "industryName", p5.name AS "jobPostName", d6.label AS "statusLabel" FROM bj_examination_record a
LEFT JOIN bj_industry i4 ON i4.id = a.industry_id LEFT JOIN bj_job_post p5 ON o2.id = a.post_id LEFT JOIN sys_dict d6 ON d6.value = a.status and d6.type='examination_record_status' and d6.del_flag='0'
a.del_flag = '0'
AND a.code like concat('%',#{code},'%')
AND a.name like concat('%', #{name},'%')
AND a.organ_id = #{organId}
AND a.status = #{status}
AND a.create_date >= #{startDate}
AND a.create_date <= #{endDate}
ORDER BY a.create_date DESC

mapper.update:
UPDATE bj_examination_record
user_id = #{user.id}, code = #{code}, name = #{name},name_pinyin=#{namePinyin}, phone_number = #{phoneNumber}, sex = #{sex}, industry_id = #{industryId}, post_id = #{postId}, birthday = #{birthday}, organ_id = #{organId}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, owner = #{owner}, package_id = #{packageId}, package_price = #{packagePrice}, id_number = #{idNumber}, status = #{status}, item_type = #{itemType},
WHERE id = #{id}

UPDATE bj_examination_record SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE bj_examination_record SET
status = #{status}
WHERE id = #{id}

UPDATE bj_examination_record SET
upload_date = now() 
upload_date = #{uploadDate}
WHERE id = #{id}
