mapper.select:
SELECT 

FROM bj_examination_record a

WHERE a.id = #{id}

SELECT

FROM bj_examination_record a

WHERE a.code = #{code}

SELECT

FROM bj_examination_record a

WHERE a.code = #{code}

SELECT 

FROM bj_examination_record a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.id_number LIKE concat('%',#{idNumber},'%')


		AND a.code = #{code}


		AND a.status = #{status}


		AND a.organId = #{organId}


		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.phone_number = #{phoneNumber}


		AND a.package_id = #{packageId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bj_examination_record a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bj_examination_record
user_id = #{user.id}, code = #{code}, name = #{name}, phone_number = #{phoneNumber}, sex = #{sex}, industry_id = #{industryId}, post_id = #{postId}, birthday = #{birthday}, organ_id = #{organId}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, owner = #{owner}, package_id = #{packageId}, package_price = #{packagePrice}, id_number = #{idNumber}, status = #{status}, item_type = #{itemType},
WHERE id = #{id}

UPDATE bj_examination_record SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
