mapper.select:
SELECT

FROM bj_examination_record_item a

WHERE a.record_id = #{recordId}
AND a.item_id = #{itemId}
AND a.examination_flag = #{examinationFlag}
AND a.result_dict_id is null  and a.del_flag=#{DEL_FLAG_NORMAL}
LIMIT 0,1

SELECT 

FROM bj_examination_record_item a


	a.del_flag = #{DEL_FLAG_NORMAL}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM bj_examination_record_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT

FROM bj_examination_record_item a


	a.del_flag=#{DEL_FLAG_NORMAL}
	AND a.record_id = #{recordId}

ORDER BY a.create_date ASC

mapper.update:
UPDATE bj_examination_record_item
record_id = #{recordId},item_id = #{itemId},need_samples = #{needSamples}, sample_code = #{sampleCode},specimen_id = #{specimenId},result_dict_id = #{resultDictId}, result_dict_name = #{resultDictName},result_flag = #{resultFlag}, result_remarks = #{resultRemarks},update_by = #{updateBy.id}, update_date = #{updateDate},remarks = #{remarks},
WHERE id = #{id}

UPDATE bj_examination_record_item
sample_code = #{needSamples}, result_dict_id = null
WHERE id = #{id}

UPDATE bj_examination_record_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE record_id = #{recordId}

UPDATE bj_examination_record_item
result_dict_id = #{resultDictId}, result_dict_name = #{resultDictName}, result_flag = #{resultFlag}, result_remarks = #{resultRemarks}, sample_code = #{sampleCode},
WHERE id = #{recordItemId}
