mapper.select:
SELECT 

FROM bj_examination_record_item a

WHERE a.id = #{id}

SELECT 

FROM bj_examination_record_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.record_id = #{recordId}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM bj_examination_record_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE bj_examination_record_item
record_id = #{recordId}, item_id = #{itemId}, result_dict_id = #{resultDictId}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks},
WHERE id = #{id}

UPDATE bj_examination_record_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE record_id = #{recordId}
