mapper.select:
SELECT 

FROM bj_examination_samples a

WHERE a.id = #{id}

SELECT 

FROM bj_examination_samples a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.examination_code = #{examinationCode}


		AND a.code = #{code}


		AND a.user_id = #{userId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bj_examination_samples a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bj_examination_samples
examination_code = #{examinationCode}, code = #{code}, item_id = #{itemId}, user_id = #{userId}, update_by = #{updateBy.id}, update_date = #{updateDate}, remarks = #{remarks}, owner = #{owner}, examination_flag = #{examinationFlag},
WHERE id = #{id}

UPDATE bj_examination_samples SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
