mapper.select:
SELECT 

FROM bj_examination_user a

WHERE a.id = #{id}

SELECT

FROM bj_examination_user a

WHERE a.del_flag = '0'
	AND a.id_number = #{idNumber}
	AND a.owner = #{owner}
limit 0,1

SELECT 

FROM bj_examination_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id_number LIKE concat('%',#{idNumber},'%')


		AND a.code = #{code}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.phone_number = #{phoneNumber}


		AND a.organ_id = #{organId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bj_examination_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bj_examination_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
