mapper.select:
SELECT 

FROM bj_industry a

WHERE a.id = #{id}

SELECT
FROM bj_industry a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.owner = #{owner}
AND a.reference_flag = #{referenceFlag}
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' concat('%',#{code},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.order_numb,a.update_date DESC

SELECT 

FROM bj_industry a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.order_numb,a.update_date DESC

mapper.update:
UPDATE bj_industry SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
