mapper.select:
SELECT 

FROM bj_organ a

WHERE a.id = #{id}

SELECT 

FROM bj_organ a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.owner = #{owner}


		AND a.reference_flag = #{referenceFlag}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bj_organ a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bj_organ SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE bj_organ SET
upload_date = now() 
upload_date = #{uploadDate}
WHERE id = #{id}
