mapper.select:
SELECT 

FROM bj_organ a

WHERE a.id = #{id}

SELECT
FROM bj_organ a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM bj_organ a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bj_organ SET 	
	name = #{name},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE bj_organ SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
