mapper.select:
SELECT 

FROM bj_package_item a

WHERE a.id = #{id}

SELECT 

FROM bj_package_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.package_id = #{examinationPackage.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM bj_package_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE bj_package_item SET 	
	package_id = #{examinationPackage.id},
	item_id = #{itemId},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE bj_package_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE package_id = #{examinationPackage.id}
